mapper.select:
SELECT csi.id, pom.company_id, csi.invoice_code, pom.company_s_name, pom.platform_service_provider, pom.id as pom_id, pom.payment_inventory, pom.many_payment, pom.is_application, csi.invoice_url, csi.tax_receipt_url, csi.invoice_print_date, IF (csi.id IS NOT NULL, '0', '1') AS invoice_stute FROM tb_crowd_sourcing_invoice csi INNER JOIN tb_crowd_sourcing_application csa ON csa.id = csi.application_id INNER JOIN tb_payment_order_many pom ON pom.id = csa.payment_order_many_id AND pom.company_id=#{param3}
AND pom.platform_service_provider=#{param2.platformServiceProvider}
AND csa.application_date>=#{param2.applicationDateOne}
AND csa.application_date <= #{param2.applicationDateTwo}
order by csi.invoice_print_date desc

SELECT csi.id,pom.company_id,csi.invoice_code,pom.company_s_name,pom.platform_service_provider,pom.id as pom_id,pom.payment_inventory,pom.many_payment,
pom.is_application,csi.invoice_url,csi.tax_receipt_url,csi.invoice_print_date,if(csi.id is not null,'1','0')as invoice_stute
FROM tb_crowd_sourcing_invoice csi
LEFT JOIN tb_crowd_sourcing_application csa ON csa.id=csi.application_id
LEFT JOIN tb_payment_order_many pom ON csa.payment_order_many_id=pom.id and csi.payment_order_many_id=pom.id
WHERE csi.payment_order_many_id is NOT null OR csi.application_id is NOT null

    AND pom.company_s_name=#{tobeinvoicedDto.companySName}


    AND pom.platform_service_provider=#{tobeinvoicedDto.platformServiceProvider}


    AND csa.application_date>=#{tobeinvoicedDto.applicationDateOne}


    AND csa.application_date <= #{tobeinvoicedDto.applicationDateTwo}

order by csi.invoice_print_date desc

SELECT csa.id AS application_id, pom.company_id, pom.company_s_name, pom.platform_service_provider, pom.id, pom.payment_inventory, pom.many_payment, pom.is_application, csa.application_state, csa.application_date FROM tb_payment_order_many pom LEFT JOIN tb_crowd_sourcing_application csa ON csa.payment_order_many_id = pom.id WHERE csa.application_state = 1 OR csa.application_state IS NULL AND pom.payment_order_status = 3 AND pom.is_application = 1
AND po.company_s_name like CONCAT('%',#{tobeinvoicedDto.companySName},'%')
AND pom.platform_service_provider=#{tobeinvoicedDto.platformServiceProvider}
AND csa.application_date>=#{tobeinvoicedDto.applicationDateOne}
AND csa.application_date <= #{tobeinvoicedDto.applicationDateTwo}
order by csa.application_date desc
