mapper.select:
SELECT csi.id,pom.company_id,csi.invoice_code,pom.company_s_name,pom.platform_service_provider,pom.id,pom.payment_inventory,pom.many_payment,
pom.is_application,csi.invoice_url,csi.tax_receipt_url,csi.invoice_print_date,if(csi.id is not null,'已开','未开')as invoice_stute
FROM tb_crowd_sourcing_invoice csi
LEFT JOIN tb_crowd_sourcing_application csa ON csa.id=csi.application_id
LEFT JOIN tb_payment_order_many pom ON csa.payment_order_many_id=pom.id and csi.payment_order_many_id=pom.id
WHERE csi.payment_order_many_id is NOT null OR csi.application_id is NOT null

    AND pom.company_id=#{merchantId}


    AND pom.platform_service_provider=#{platformServiceProvider}


    AND csa.application_date>=#{applicationDateOne}


    AND csa.application_date <= #{applicationDateTwo}

SELECT csi.id,pom.company_id,csi.invoice_code,pom.company_s_name,pom.platform_service_provider,pom.id,pom.payment_inventory,pom.many_payment,
pom.is_application,csi.invoice_url,csi.tax_receipt_url,csi.invoice_print_date,if(csi.id is not null,'已开','未开')as invoice_stute
FROM tb_crowd_sourcing_invoice csi
LEFT JOIN tb_crowd_sourcing_application csa ON csa.id=csi.application_id
LEFT JOIN tb_payment_order_many pom ON csa.payment_order_many_id=pom.id and csi.payment_order_many_id=pom.id
WHERE csi.payment_order_many_id is NOT null OR csi.application_id is NOT null

    AND pom.company_s_name=#{companySName}


    AND pom.platform_service_provider=#{platformServiceProvider}


    AND csa.application_date>=#{applicationDateOne}


    AND csa.application_date <= #{applicationDateTwo}

SELECT  csa.id as application_id,pom.company_id,pom.company_s_name,pom.platform_service_provider,pom.id,pom.payment_inventory,pom.many_payment,pom.is_application,csa.application_state,csa.application_date
FROM tb_payment_order_many pom
LEFT JOIN tb_crowd_sourcing_application csa ON csa.payment_order_many_id=pom.id
WHERE csa.application_state=1 or csa.application_state is null
AND pom.payment_order_status=3

    AND po.company_s_name like CONCAT('%',#{companySName},'%')


    AND pom.platform_service_provider=#{platformServiceProvider}


    AND csa.application_date>=#{applicationDateOne}


    AND csa.application_date <= #{applicationDateTwo}
