mapper.select:
SELECT invapp.id, po.company_s_name, po.platform_service_provider, invapp.application_desc, po.is_invoice, invapp.application_date, po.id as pid, po.payment_inventory, po.subpackage_payment FROM tb_payment_order AS po LEFT JOIN tb_application_payment AS app ON app.payment_order_id = po.id LEFT JOIN tb_invoice_application as invapp ON invapp.id=app.invoice_application_id LEFT JOIN tb_invoice as i ON i.application_id=invapp.id WHERE po.payment_order_status = 6 and is_not_invoice is null AND po.company_id=#{param3}
AND po.tax_id=#{param2.platformServiceProvider}
AND invapp.application_date>=#{param2.applicationDateOne}
AND invapp.application_date <= #{param2.applicationDateTwo}
ORDER BY invapp.application_date DESC

SELECT i.id, i.application_id, i.invoice_code, po.company_s_name, po.platform_service_provider, invapp.application_desc, invapp.application_state, i.invoice_print_date, i.invoice_url, i.tax_receipt_url, po.is_invoice, po.id as pid, po.payment_inventory, po.turnkey_project_payment FROM tb_payment_order AS po LEFT JOIN tb_application_payment AS app ON app.payment_order_id = po.id LEFT JOIN tb_invoice_application as invapp ON invapp.id=app.invoice_application_id LEFT JOIN tb_invoice as i ON i.application_id=invapp.id WHERE po.payment_order_status = 6 and invapp.application_state = 3 AND po.company_id=#{param3}
AND po.platform_service_provider=#{param2.platformServiceProvider}
AND invapp.application_date>=#{param2.applicationDateOne}
AND invapp.application_date <= #{param2.applicationDateTwo}
ORDER BY i.invoice_print_date DESC

SELECT app.invoice_application_id, po.company_s_name, po.platform_service_provider, invapp.application_desc, invapp.application_state, invapp.application_date, po.is_invoice, po.id as pid, po.payment_inventory, po.subpackage_payment FROM tb_invoice_application invapp INNER JOIN tb_application_payment app ON app.invoice_application_id = invapp.id INNER JOIN tb_payment_order po ON po.id = app.payment_order_id WHERE invapp.application_state != 3
AND po.company_s_name like CONCAT('%',#{tobeinvoicedDto.companySName},'%')
AND po.tax_id=#{tobeinvoicedDto.platformServiceProvider}
AND invapp.application_date>=#{tobeinvoicedDto.applicationDateOne}
AND invapp.application_date <= #{tobeinvoicedDto.applicationDateTwo}
order by invapp.application_date desc

SELECT i.id,i.application_id,i.invoice_code,po.company_s_name,po.platform_service_provider,po.is_invoice,ia.application_desc,i.invoice_url, i.tax_receipt_url,ia.application_state,i.invoice_print_date,po.id as pid,po.payment_inventory,po.turnkey_project_payment FROM tb_invoice as i INNER JOIN tb_invoice_application as ia ON i.application_id=ia.id INNER JOIN tb_application_payment as ap ON ia.id = ap.invoice_application_id INNER JOIN tb_payment_order as po ON ap.payment_order_id=po.id WHERE ia.application_state=3
AND po.company_s_name like CONCAT('%',#{tobeinvoicedDto.companySName},'%')
AND po.tax_id=#{tobeinvoicedDto.platformServiceProvider}
AND ia.application_date>=#{tobeinvoicedDto.applicationDateOne}
AND ia.application_date <= #{tobeinvoicedDto.applicationDateTwo}
order by i.invoice_print_date desc

SELECT i.id,po.company_s_name,po.platform_service_provider,i.invoice_url,i.tax_receipt_url,po.is_subpackage,po.payment_date,po.id as pid,po.payment_inventory,po.turnkey_project_payment FROM tb_invoice as i INNER JOIN tb_invoice_application as ia ON i.application_id=ia.id INNER JOIN tb_application_payment as ap ON ap.invoice_application_id=ia.id INNER JOIN tb_payment_order as po ON po.id=ap.payment_order_id WHERE ia.application_state = 3 AND po.is_subpackage=0
AND po.company_s_name like CONCAT('%',#{tobeinvoicedDto.companySName},'%')
AND po.tax_id=#{tobeinvoicedDto.platformServiceProvider}
AND invapp.application_date>=#{tobeinvoicedDto.applicationDateOne}
AND invapp.application_date <= #{tobeinvoicedDto.applicationDateTwo}
order by i.invoice_print_date desc
