mapper.select:
SELECT a.id, a.company_name, b.link_name, b.link_mobile, ( CASE WHEN a.agent_id IS NULL THEN ( SELECT real_name FROM tb_managers WHERE id = a.sales_man_id ) ELSE CONCAT( ( SELECT real_name FROM tb_managers WHERE id = a.sales_man_id ), '/', ( SELECT real_name FROM tb_managers c INNER JOIN tb_agent d ON c.id = d.managers_id WHERE c.id = a.agent_id ) ) END ) structure, a.contract, a.company_status, a.create_date FROM tb_company_info a LEFT JOIN tb_linkman b ON a.id = b.company_id AND b.is_not = 0
a.id in
#{item}
and a.audit_status = #{auditStatus}
and a.company_id = #{merchantId}
and a.company_name = CONCAT('%',#{merchantName},'%')
and b.link_mobile = #{linkMobile}
order by a.create_date desc

select * from tb_merchant_payment_list
where company_id = #{merchantId}
ORDER BY payment_date desc
