mapper.select:
SELECT DISTINCT t3.id, t3.tax_name, t3.credit_code, t3.link_man, t3.link_mobile, t3.tax_create_date FROM tb_merchant t1
INNER JOIN tb_company_tax t2 ON t1.company_id = t2.company_id INNER JOIN tb_tax t3 ON t2.tax_id = t3.id
WHERE t1.merchant_id = #{merchantId}

SELECT DISTINCT t3.id, t3.tax_name, t3.credit_code, t3.link_man, t3.link_mobile, t3.tax_create_date FROM tb_merchant_unionpay t1
INNER JOIN tb_tax_unionpay t2 ON t1.tax_unionpay_id = t2.id INNER JOIN tb_tax t3 ON t2.tax_id = t3.id
WHERE t1.merchant_id = #{merchantId}
