select SUM(real_money) 'total_money' from tb_payment_order where
payment_order_status >= 2
and tax_id = #{taxId}

SELECT
COUNT(1) as 'order_count',
SUM(a.real_money) as 'bill_money'
FROM tb_payment_inventory a
WHERE YEAR(a.create_date) = #{year}
AND a.worker_id = #{workerId}
