mapper.select:
select SUM(real_money) 'total_money' from tb_payment_order_many where
date_sub(curdate(), INTERVAL 30 DAY) <= date(`payment_date`)
and payment_order_status >= 2 and company_id = #{merchantId}

select
id,company_s_name, platform_service_provider, company_contract, payment_inventory, many_payment, task_id, acceptance_certificate,payment_order_status, create_date
from tb_payment_order_many

    company_id = #{merchantId}

        and id like CONCAT('%',#{id},'%')


        and tax_id = #{taxId}


        and date(create_date) >= #{beginDate}


        and date(create_date) <= #{endDate}

SELECT pom.company_s_name,pom.platform_service_provider,
pom.id,pom.payment_inventory,pom.many_payment,csi.application_date,pom.is_application
FROM tb_payment_order_many as pom
LEFT JOIN tb_crowd_sourcing_application as csi ON csi.payment_order_many_id=pom.id
WHERE pom.payment_order_status=3

    AND pom.merchant_id=#{merchantId}


    AND pom.platform_service_provider=#{platformServiceProvider}


    AND csi.invapp.application_date>={applicationDate}


    AND csi.invapp.application_date <= {applicationDate}

SELECT  worker_id,worker_name,mobile_code,id_card_code,bank_code,real_money
FROM tb_payment_inventory
WHERE payment_order_id=#{id}

select SUM(real_money) 'total_money' from tb_payment_order_many where
payment_order_status >= 2 and company_id in

    #{merchantIds}

select * from tb_payment_order_many where to_days(payment_date) = to_days(now())
and payment_order_status >= 2 and company_id in

    #{merchantIds}

SELECT * FROM tb_payment_order_many WHERE YEARWEEK(date_format(payment_date,'%Y-%m-%d')) = YEARWEEK(now())
and payment_order_status >= 2 and company_id in

    #{merchantIds}

SELECT * FROM tb_payment_order_many WHERE DATE_FORMAT( payment_date, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )
and payment_order_status >= 2 and company_id in

    #{merchantIds}

select * from tb_payment_order_many where YEAR(payment_date)=YEAR(NOW())
and payment_order_status >= 2 and company_id in

    #{merchantIds}

select
id,company_s_name, platform_service_provider, company_contract, payment_inventory, many_payment, task_id, acceptance_certificate,payment_order_status, create_date
from tb_payment_order_many

    company_id in

        #{merchantIds}


        and company_s_name like CONCAT('%',#{merchantName},'%')


        and tax_id like CONCAT('%',#{taxId},'%')


        and date(create_date) >= #{beginDate}


        and date(create_date) <= #{endDate}

select SUM(real_money) 'total_money' from tb_payment_order where
payment_order_status >= 2
and tax_id = #{taxId}
