mapper.select:
select SUM(real_money) 'total_money' from tb_payment_order where
date_sub(curdate(), INTERVAL 30 DAY) <= date(`payment_date`)
and payment_order_status = 2 and merchant_id = #{merchantId}

select
id,company_s_name, tax_id, platform_service_provider, company_contract, payment_inventory,
turnkey_project_payment, subpackage_payment, task_id, acceptance_certificate,payment_order_status, create_date
from tb_payment_order

    merchant_id = #{merchantId}

        and id = #{id}


        and tax_id = #{taxId}


        and date(create_date) >= #{beginDate}


        and date(create_date) <= #{endDate}


LIMIT #{page},#{pageSize}

