payment_order_status >= 2 and merchant_id in

    #{merchantIds}

select * from tb_payment_order where to_days(payment_date) = to_days(now())
and payment_order_status >= 2 and merchant_id in

    #{merchantIds}

SELECT * FROM tb_payment_order WHERE YEARWEEK(date_format(payment_date,'%Y-%m-%d')) = YEARWEEK(now())
and payment_order_status >= 2 and merchant_id in

      #{merchantIds}

SELECT * FROM tb_payment_order WHERE DATE_FORMAT( payment_date, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )
and payment_order_status >= 2 and merchant_id in

    #{merchantIds}

select * from `tb_payment_order` where YEAR(payment_date)=YEAR(NOW())
and payment_order_status >= 2 and merchant_id in

   #{merchantIds}

