date_sub(curdate(), INTERVAL 30 DAY) <= date(`payment_date`)
and payment_order_status = 6 and company_id = #{merchantId}

select
id,company_s_name, tax_id, platform_service_provider, company_contract, payment_inventory, turnkey_project_payment, subpackage_payment, task_id,
acceptance_certificate, payment_order_status, reasons_for_rejection, trade_fail_reason, create_date
from tb_payment_order

    company_id = #{merchantId}

        and id = #{id}


        and tax_id = #{taxId}


        and date(create_date) >= #{beginDate}


        and date(create_date) <= #{endDate}


ORDER BY create_date DESC

