SELECT
COUNT(1) as order_count,
SUM(a.real_money) as bill_money
FROM tb_payment_inventory a
WHERE YEAR(a.create_date) = #{year}
AND a.worker_id = #{workerId}

SELECT IFNULL(SUM(po.real_money),0) FROM tb_payment_order po WHERE po.payment_order_status = 6
AND DATE_SUB(CURDATE(), INTERVAL #{param3} DAY) <= date(payment_date)
AND po.company_id = #{param1}
AND po.tax_id = #{param2}

SELECT COUNT(1) FROM tb_payment_order po WHERE po.is_not_invoice = 1
AND po.company_id = #{param1}
AND po.tax_id = #{param2}

SELECT IFNULL(SUM(po.service_money), 0) FROM tb_payment_order po WHERE po.payment_order_status = 6
AND po.company_id = #{param1}
AND po.tax_id = #{param2}

SELECT IFNULL(SUM(po.real_money + po.service_money),0) FROM tb_payment_order po WHERE po.is_not_invoice = 1
AND po.company_id = #{param1}
AND po.tax_id = #{param2}
