mapper.select:
SELECT id, regulator_name, user_name, linkman, link_mobile, status, create_date FROM tb_regulator
AND regulator_name like CONCAT('%',#{regulatorName},'%')
AND date(create_date) >= #{startDate}
AND date(create_date) <= #{endDate}

mapper.sql:
((SELECT a.worker_id, IFNULL(total_order_count,0) AS 'total_order_count', IFNULL(total_money,0) AS 'total_money', IFNULL(many_order_count,0) AS 'many_order_count', IFNULL(many_money,0) AS 'many_money', IFNULL(many_tax_money,0) AS 'many_tax_money', IFNULL(total_tax_money,0) AS 'total_tax_money' FROM( SELECT worker_id, count(1) AS 'total_order_count', sum(real_money) AS 'total_money', sum(tax_amount) AS 'total_tax_money' FROM tb_payment_inventory WHERE package_status = 0 AND payment_order_id in
#{item}
GROUP BY worker_id , package_status) a LEFT JOIN ( SELECT worker_id, count(1) as 'many_order_count', sum(real_money) as 'many_money', sum(tax_amount) AS 'many_tax_money' FROM tb_payment_inventory WHERE package_status = 1 AND payment_order_id in
#{item}
GROUP BY worker_id , package_status) b ON a.worker_id = b.worker_id ) UNION ( SELECT c.worker_id, IFNULL(total_order_count,0) AS 'total_order_count', IFNULL(total_money,0) AS 'total_money', IFNULL(many_order_count,0) AS 'many_order_count', IFNULL(many_money,0) AS 'many_money', IFNULL(many_tax_money,0) AS 'many_tax_money', IFNULL(total_tax_money,0) AS 'total_tax_money' FROM ( SELECT worker_id, count(1) as 'total_order_count', sum(real_money) as 'total_money', sum(tax_amount) AS 'total_tax_money' FROM tb_payment_inventory WHERE package_status = 0 AND payment_order_id in
#{item}
GROUP BY worker_id , package_status) c RIGHT JOIN( SELECT worker_id, count(1) as 'many_order_count', sum(real_money) as 'many_money', sum(tax_amount) AS 'many_tax_money' FROM tb_payment_inventory WHERE package_status = 1 AND payment_order_id in
#{item}
GROUP BY worker_id , package_status) d ON c.worker_id = d.worker_id ))

