mapper.sql:
SELECT ci.id, ci.company_name, ci.contract, if(po.count_total_order is null,0,po.count_total_order)as count_total_order, if(po.count_total_money is null,0,po.count_total_money)as count_total_money, if(pom.count_many_order is null,0,pom.count_many_order)as count_many_order, if(pom.count_many_money is null,0,pom.count_many_money)as count_many_money, ci.audit_status, ci.create_date FROM tb_company_info ci LEFT JOIN (SELECT COUNT(1) AS 'count_total_order', SUM(real_money) AS 'count_total_money', company_id FROM tb_payment_order WHERE tax_id IN
#{item}
AND payment_order_status=6 GROUP BY company_id) po ON ci.id = po.company_id LEFT JOIN (SELECT COUNT(1) AS 'count_many_order', SUM(real_money) AS 'count_many_money', company_id FROM tb_payment_order_many WHERE tax_id IN
#{item}
AND payment_order_status=3 GROUP BY company_id) pom ON ci.id = pom.company_id
