mapper.select:
SELECT t.id,t.tax_name,(SELECT COUNT(1) FROM tb_payment_order WHERE tax_id=t.id AND payment_order_status=6) as payment_order_num, (SELECT SUM(real_money) FROM tb_payment_order WHERE tax_id=t.id AND payment_order_status=6) as payment_order_money, (SELECT COUNT(1) FROM tb_payment_order_many WHERE tax_id=t.id AND payment_order_status=3) as payment_order_many_num, (SELECT SUM(real_money) FROM tb_payment_order_many WHERE tax_id=t.id AND payment_order_status=3) as payment_order_many_money, rt.`status` as tax_status,t.create_date from tb_regulator_tax rt INNER JOIN tb_tax t ON t.id=rt.tax_id where rt.regulator_id=#{regulatorId} and rt.`status`!=1
AND t.tax_name like CONCAT('%',#{tax.serviceProviderName},'%')
AND t.id = #{tax.serviceProvideId}
AND t.create_date >= #{tax.startDate}
AND t.create_date <= #{tax.endDate}
order by t.create_date desc
