mapper.select:
select f.id, f.tax_name, f.link_man, f.link_mobile, e.total_status, e.many_status, f.tax_status, f.create_date from tb_tax f LEFT JOIN ((select (case when a.tax_id is null then b.tax_id else a.tax_id end) 'tax_id', (case when a.id is null then 0 else 1 end) 'total_status',(case when b.id is null then 0 else 1 end) 'many_status' from (select * from tb_tax_package where package_status = 0) a left join (select * from tb_tax_package where package_status = 1) b on a.tax_id = b.tax_id ) union (select (case when c.tax_id is null then d.tax_id else c.tax_id end) 'tax_id' , (case when c.id is null then 0 else 1 end) 'total_status',(case when d.id is null then 0 else 1 end) 'many_status' from (select * from tb_tax_package where package_status = 0) c right join (select * from tb_tax_package where package_status = 1) d on c.tax_id = d.tax_id ))e on f.id = e.tax_id
INNER JOIN tb_company_tax ct ON ct.tax_id=f.id
where 1=1
and ct.company_id=#{merchantId}
and tax_name like CONCAT('%',#{taxName},'%')
and date(create_date) >= #{startDate}
and date(create_date) <= #{endDate}
GROUP BY f.id

select payment_order_id,merchant_name,tax_name,package_status,real_money,is_invoice,payment_date from tb_merchant_payment_list where payment_order_id in
#{item}
ORDER BY payment_date desc

SELECT id,platform_service_provider,company_s_name,if(TRUE,'0','1') as type,company_contract,payment_inventory,real_money,payment_order_status,payment_date from tb_payment_order WHERE tax_id=#{taxId}
UNION SELECT id,platform_service_provider,company_s_name,if(FALSE,'0','1') as type,company_contract,payment_inventory,real_money,payment_order_status,payment_date FROM tb_payment_order_many WHERE tax_id=#{taxId}
ORDER BY id
