mapper.select:
select
a.id, account_name, mobile_code, idcard_code, bank_code, attestation, agreement_sign ,a.create_date
from tb_worker a join tb_company_worker b on a.id = b.worker_id

    b.company_id = #{merchantId}

    and account_name like CONCAT('%',#{accountName},'%')


    and mobile_code = #{mobileCode}

SELECT DISTINCT w.id, w.account_name, w.mobile_code, w.idcard_code, w.bank_code, w.attestation, w.agreement_url, w.create_date FROM tb_worker w INNER JOIN tb_company_worker cw ON cw.worker_id=w.id AND cw.company_id in
#{item}
INNER JOIN tb_payment_inventory pi ON pi.worker_id = w.id LEFT JOIN ( SELECT id AS payment_order_id FROM tb_payment_order WHERE company_id in
#{item}
UNION SELECT id AS payment_order_id FROM tb_payment_order_many WHERE company_id in
#{item}
) po ON po.payment_order_id = pi.payment_order_id WHERE w.attestation = 1 AND w.agreement_sign = 2
ORDER BY w.create_date DESC

SELECT po.payment_order_id, po.tax_id, po.company_id, po.tax_name, po.company_s_name as merchant_name, pi.worker_name, pi.real_money, ifnull(po.is_not_invoice, 0) as invoice_status, pi.package_status, pi.update_date as payment_date FROM ( SELECT id AS payment_order_id, company_id AS company_id, tax_id AS tax_id, platform_service_provider AS tax_name, company_s_name AS company_s_name, is_not_invoice AS is_not_invoice FROM tb_payment_order WHERE payment_order_status = 6 UNION SELECT id AS payment_order_id, company_id AS company_id, tax_id AS tax_id, platform_service_provider AS tax_name, company_s_name AS company_s_name, is_not_invoice AS is_not_invoice FROM tb_payment_order_many WHERE payment_order_status = 3 ) AS po INNER JOIN tb_payment_inventory pi ON pi.payment_order_id = po.payment_order_id INNER JOIN tb_regulator_tax rt ON po.tax_id=rt.tax_id INNER JOIN tb_regulator r ON r.id = rt.regulator_id WHERE pi.worker_id = #{param3} AND r.id= #{param2}
AND po.tax_name like CONCAT('%',#{taxName},'%')
AND po.company_s_name like CONCAT('%',#{companyName},'%')
AND pi.update_date >= #{startDate}
AND pi.update_date <= #{endDate}
ORDER BY pi.update_date DESC

SELECT pi.payment_order_id, po.company_s_name, po.platform_service_provider, pi.worker_name, pi.package_status, pi.real_money AS task_money, pi.payment_status, pi.create_date FROM tb_payment_inventory pi LEFT JOIN ( SELECT id AS payment_order_id, company_id as company_id, company_s_name AS company_s_name, platform_service_provider AS platform_service_provider FROM tb_payment_order UNION SELECT id AS payment_order_id, company_id as company_id, company_s_name AS company_s_name, platform_service_provider AS platform_service_provider FROM tb_payment_order_many ) po ON po.payment_order_id = pi.payment_order_id WHERE pi.worker_id =#{param2} AND pi.payment_status = 1
AND company_id= #{param3}
order by pi.create_date desc
